// Transform Ticketmaster events to PrismIQ format
const response = $input.first().json;
const rawEvents = response._embedded?.events || [];

// Map Ticketmaster data to PrismIQ format
const events = rawEvents.map(event => {
  // Determine event type from classification
  const segment = event.classifications?.[0]?.segment?.name?.toLowerCase() || "";
  let type = "other";

  if (segment.includes("music")) {
    type = "concert";
  } else if (segment.includes("sports")) {
    type = "sports";
  } else if (segment.includes("arts") || segment.includes("theatre")) {
    type = "concert"; // Treat arts/theatre like concerts
  }

  return {
    name: event.name,
    type: type,
    venue: event._embedded?.venues?.[0]?.name || "Unknown Venue",
    start_time: event.dates?.start?.dateTime || new Date().toISOString(),
    radius_miles: 5.0
  };
});

// Filter to only events happening in next 24 hours
const now = new Date();
const tomorrow = new Date(now.getTime() + 24 * 60 * 60 * 1000);

const upcomingEvents = events.filter(e => {
  const eventTime = new Date(e.start_time);
  return eventTime >= now && eventTime <= tomorrow;
});

return [{
  json: {
    events: upcomingEvents
  }
}];
